package Inheritence;


class Animal{
	void eat()
	{
		System.out.println("eating..");
	}
}
public class single_level extends Animal{

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Animal g=new Animal();
		g.eat();
	}
	
}



